<script lang="ts">
	import { clickoutside } from '@svelte-put/clickoutside';
	import type { ResolveTrigger } from '@svelte-put/modal';
	import { createQrPngDataUrl } from '@svelte-put/qr';
	import { qr } from '@svelte-put/qr/svg';
	import { shortcut } from '@svelte-put/shortcut';
	import { createEventDispatcher, onDestroy, onMount } from 'svelte';
	import { fade, fly } from 'svelte/transition';

	import { getLockScrollContext } from '$lib/contexts/lockscroll';
	import { getSettingsContext } from '$lib/contexts/settings';

	const DEFAULT_TEXTS = {
		title: 'QR Code',
		description: 'Show your friends this QR code for quick access to the blog post.',
		filename: 'qr.png',
		download: 'Download',
	};

	export let data: string;
	export let texts = DEFAULT_TEXTS;

	const dispatch = createEventDispatcher<{
		resolve: {
			trigger: ResolveTrigger;
		};
	}>();

	function escape() {
		dispatch('resolve', { trigger: 'escape' });
	}

	function clickOutside() {
		dispatch('resolve', { trigger: 'clickoutside' });
	}

	function dismiss() {
		dispatch('resolve', { trigger: 'x' });
	}

	const BASE64_LOGOS = {
		light:
			'data:image/svg+xml;base64,',
		dark: 'data:image/svg+xml;base64,',
	};
	const { colorScheme } = getSettingsContext();
	$: preferred = colorScheme.preferred;

	$: logo = BASE64_LOGOS[$preferred];
	$: qrConfig = {
		data,
		shape: 'circle',
		logo,
		logoRatio: 89 / 100,
		margin: 2,
	} as const;

	let svg: SVGElement;
	let href = '';

	let container: HTMLElement;
	const lockScrollStore = getLockScrollContext();
	onMount(async () => {
		href = await createQrPngDataUrl({
			...qrConfig,
			logo: BASE64_LOGOS.light,
			width: 500,
			height: 500,
			backgroundFill: 'white',
		});
		if (lockScrollStore) $lockScrollStore = true;
	});

	onDestroy(() => {
		if (lockScrollStore) $lockScrollStore = false;
	});
</script>

<svelte:window use:shortcut={{ trigger: { key: 'Escape', callback: escape } }} />

<div
	class="absolute inset-0 grid h-full w-full max-w-full place-items-center"
	bind:this={container}
>
	<div aria-disabled class="c-backdrop" transition:fade={{ duration: 150 }}></div>
	<div
		class="relative rounded bg-bg p-6 tb:p-8"
		use:clickoutside={{ limit: { parent: container } }}
		on:clickoutside={clickOutside}
		transition:fly={{ duration: 200, y: 20 }}
	>
		<button class="absolute right-6 top-6 hover:text-primary active:scale-95" on:click={dismiss}>
			<svg inline-src="lucide/x" width="24" height="24" />
		</button>
		<div class="space-y-6">
			<p class="c-text-h3 font-medium after:mt-2 after:separator">{texts.title}</p>
			<p class="">{texts.description}</p>
			<svg
				xmlns="http://www.w3.org/2000/svg"
				bind:this={svg}
				use:qr={qrConfig}
				width="320"
				height="320"
				class="mx-auto max-w-full"
			/>
			<a class="c-btn mx-auto" {href} download="{texts.filename}.png">
				{texts.download}
			</a>
		</div>
	</div>
</div>
